import React, { useState, useEffect } from 'react';
import Navbar from './Navbar';
import Footer from './Footer';

const Gloves = () => {
  const [cartItems, setCartItems] = useState(() => {
    const savedCartItems = localStorage.getItem('cartItems');
    return savedCartItems ? JSON.parse(savedCartItems) : [];
  });

  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch("http://localhost:3001/api/gloves");
        const data = await response.json();
        setProducts(data);
      } catch (error) {
        console.error("Error fetching products:", error);
      }
    };

    fetchProducts();
  }, []);

  const addToCart = (product) => {
    setCartItems((prevCartItems) => {
      const updatedCartItems = [...prevCartItems, product];
      localStorage.setItem('cartItems', JSON.stringify(updatedCartItems));
      return updatedCartItems;
    });
  };

  const removeFromCart = (productId) => {
    setCartItems((prevCartItems) => {
      const updatedCartItems = prevCartItems.filter(item => item._id !== productId);
      localStorage.setItem('cartItems', JSON.stringify(updatedCartItems));
      return updatedCartItems;
    });
  };

  return (
    <div className="product-container">
      <Navbar cartItems={cartItems} />
      <div className="products">
        {products.map((product) => (
          <div key={product._id} className="product">
            <img src={product.image} alt={product.name} />
            <p style={{ fontFamily: 'fantasy' }}>{product.name}</p>
            <p style={{ color: 'red', fontWeight: 'bold', fontSize: '30px' }}>{product.price} DT</p>
            <button onClick={() => addToCart(product)}>Add to Cart</button>
            <button onClick={() => removeFromCart(product._id)}>Remove from Cart</button>
          </div>
        ))}
      </div>
      <Footer />
    </div>
  );
};

export default Gloves;
